import React, { useRef } from 'react';
import './Home.scss';
import { Button } from "../../components/Button/Button"

export const Home: React.FunctionComponent = () => {

    const firstnameInputRef = useRef<HTMLInputElement>(null)
    const lastnameInputRef = useRef<HTMLInputElement>(null)

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();

        if(firstnameInputRef && firstnameInputRef.current)
            console.log(firstnameInputRef.current.value);

        if(lastnameInputRef && lastnameInputRef.current)
            console.log(lastnameInputRef.current.value);
      };

    return (
        <div className="Home">
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <input type="text" 
                            name="firstname" 
                            className='form-control' 
                            placeholder="Enter your first name" 
                            ref={firstnameInputRef}></input>
                </div>

                <div className="form-group">
                    <input type="text" 
                            name="lastname" 
                            className='form-control' 
                            placeholder="Enter your last name" 
                            ref={lastnameInputRef}></input>
                </div>

                <div className="btn-wrapper">
                    <Button type={'submit'} text='Log in' className="login-btn"/>
                </div>
            </form>
        </div>
    );
}
