import React, { useState, useEffect } from 'react';
import { Button } from "../../components/Button/Button"
import { Input } from "../../components/Input/Input"
import "./Login.scss"

export const Login: React.FunctionComponent = () => {

    const initialFormData = Object.freeze({
        username: "",
        password: "",
        buttonDisabled: false
      });

    const [formData, updateFormData] = React.useState(initialFormData);

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        updateFormData({
          ...formData,

          [e.target.name]: e.target.value.trim()
        });
      };

    const handleSubmit = async (e: React.FormEvent<HTMLFormElement>): Promise<void> => {
        e.preventDefault()
        // console.log(formData);
        // setUserName(formData.username)
      };

    return (
        <form className="Login" onSubmit={handleSubmit}>
            <h3 className="login-title">Login</h3>
            <input type="text" placeholder="Username" className="login-input" onChange={handleChange}/>
            <input type="password" placeholder="Password" className="login-input" onChange={handleChange}/>
            <button type="submit" className="submit-btn">Submit</button>
            {/* <Input className={"login-input"} type={"text"} placeholder={"Username"} changeHandler={handleChange}/>
            <Input className={"login-input"} type={"password"} placeholder={"Password"} changeHandler={handleChange}/> */}
            {/* <Button className={"submit-btn"} text={"Submit"} clickHandler={changeHandler}/> */}
        </form>
    );
}
