import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import { Hello } from "./components/hello/hello"
import { Input } from "./components/input/input"


// Homework:
// - Change name -> show input field
// - align everything to centre


function App() {
  const [clicked, setClicked] = useState<Boolean>();
  const [name, setName] = useState<String>();
  const [nameChange, setnameChange] = useState<Boolean>();

  const btnClickLoginHandler = () => { 
    console.log("Button 1 clicked") 
    setClicked(true);
  }

  const btnClickNameHandler = () => { 
    console.log("Button 2 clicked") 
    setName("Sanzhar");
    setnameChange(true);
  }

  return (
    <div className="App">
      <div className="App-wrapper">
        { clicked ? <Hello name={"Sanzhar"} /> : null}
        <button className="App-login-btn" onClick = {btnClickLoginHandler}>Log in</button>
        { nameChange ? <input type="text" className="App-name-input"></input> : <button className="App-login-btn" onClick = {btnClickNameHandler}>Change name</button>}
      </div>
    </div>
  );
}

export default App;
