import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import { Hello } from "./components/hello/hello"
import { Input } from "./components/input/input"


// Homework:
// - Change name -> show input field
// - align everything to centre


function App() {
  const [clicked, setClicked] = useState<Boolean>();
  const [name, setName] = useState<String>();
  const [nameChange, setnameChange] = useState<Boolean>();
  const [log, setLog] = useState<String>("Log in");
  const [username, setUserName] = useState<String>();

  const btnClickLoginHandler = () => { 
    console.log("Log in clicked") 
    setClicked(true);
    setLog(log === "Log in" ? "Log out" : "Log in")
  }

  const btnClickNameHandler = () => { 
    console.log("Change name clicked") 
    setName(username);
    setnameChange(true);
  }

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    e.preventDefault()
    console.log("Value: " + e.target.value)
    setUserName(e.target.value)
    // setnameChange(false)
  }

  const inputHandler = <form action="submit" className="inputForm"><input type="text" className="inputField" onSubmit = {handleChange}></input><button className="input-btn">Change</button></form>
  // <form action="submit" className="inputForm">
  //   <input type="text" className="inputField" onSubmit = {handleChange}>
  //   </input><button className="input-btn">Change</button>
  // </form>


  return (
    <div className="App">
      <div className="App-wrapper">
        { clicked && username ? <Hello name={ username } picture = { "https://via.placeholder.com/200x200.png" } link = {"#"}/> : null }
        <button className="App-login-btn" onClick = {btnClickLoginHandler}>{ username ? log : "Log in" }</button>
        { nameChange ? inputHandler : <button className="App-login-btn" onClick = { btnClickNameHandler }>Change name</button>}
      </div>
    </div>
  );
}

export default App;
