import React, { useState, useEffect } from 'react';
import YouTube from 'react-youtube'
import "./Room.scss"
import { Chat } from "../chat/Chat"
import { RoomHeader } from "../../components/roomHeader/RoomHeader"
import { UserContext } from '../../services/context';
import { Videos } from '../videos/Videos';
import { useParams } from 'react-router-dom';
import { Button } from '../../components/Button/Button';

enum PlayerStates {
    PLAYING = 'PLAYING',
    PAUSED = 'PAUSED',
}

export const Room: React.FunctionComponent = () => {

    const { id } = useParams();

    const [player, setPlayer] = useState<any>();

    const [playerState, setPlayerState] = useState<PlayerStates>();

    const onVideoInit = (e: {target: any}) => {
        setPlayer(e.target);
    }

    const toggleVideo = () => {
        if (playerState !== PlayerStates.PLAYING) {
            player?.playVideo();
            setPlayerState(PlayerStates.PLAYING);
        } else if(playerState === PlayerStates.PLAYING) {
            player?.pauseVideo();
            setPlayerState(PlayerStates.PAUSED);
        }
    }

    useEffect(() => {
        setPlayerState(player?.getPlayerState());
    }, [player?.getPlayerState()]);

    return (
        <div className="Room">
            <div className="room-header">
                <RoomHeader/>
            </div>

            <div className="room-body">
                <div className="video-wrapper">
                    <YouTube videoId={id} className="video" onReady={onVideoInit}/>

                    <div className="video-controls">
                        <Button text={!player || playerState !== 1 ? 'Play' : 'Pause'} type='button' clickHandler={toggleVideo}/>
                    </div>
                </div>

                <div className="chat-container">
                    <UserContext.Consumer>
                        {(value) => (
                            <Chat user={value?.user}/>
                        )}
                    </UserContext.Consumer>
                </div>
            </div>
        </div>
    );
}
