import React, { useState } from 'react';
import './Home.scss';
import { Button } from "../../components/Button/Button"

interface FormError {
    isEmpty?: boolean,
    isInvalid?: boolean
}
interface UserFormError {
    firstname: FormError,
    lastname: FormError
}

export const Home: React.FunctionComponent = () => {

    const [userInfo, setUserInfo] = useState<{firstname: string; lastname: string;} | null>(null);
    const [userFormError, setUserFormError] = useState<UserFormError>({firstname: {}, lastname: {}});

    const changeHandler = (field: 'firstname' | 'lastname', value: string) => {
        const newVal = {
            ...userInfo,
            [field]: value
        };
        setUserInfo(newVal as any);
    };

    const submitHandler = (e: React.FormEvent) => {
        e.preventDefault();
        console.log(userInfo)
      };

    return (
        <div className="Home">
            <form onSubmit={submitHandler}>
                <div className="form-group">
                    <input type="text" 
                            name="firstname" 
                            className='form-control' 
                            placeholder="Enter your first name" 
                            onChange={(e) => changeHandler('firstname', e.target.value)}></input>
                    <div className="form-error">
                        { userFormError.firstname.isEmpty ? "This field is required" : ''}
                        { userFormError.firstname.isInvalid ? "Entered value is invalid" : ''}
                    </div>
                </div>
                <div className="form-group">
                    <input type="text" 
                            name="lastname" 
                            className='form-control' 
                            placeholder="Enter your last name"
                            onChange={(e) => changeHandler('lastname', e.target.value)}></input>
                    <div className="form-error">
                        { userFormError.lastname.isEmpty ? "This field is required" : ''}
                        { userFormError.lastname.isInvalid ? "Entered value is invalid" : ''}
                    </div>
                </div>

                <div className="btn-wrapper">
                    <Button type={'submit'} text='Log in' className="login-btn"/>
                </div>
            </form>
        </div>
    );
}
