import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import "./Room.scss"
import YouTube from 'react-youtube'
import { Chat } from "../chat/Chat"
import { RoomHeader } from "../../components/roomHeader/RoomHeader"
import { UserContext } from '../../services/context';
import { Button } from '../../components/Button/Button';
import { FaPlayCircle, FaPauseCircle, FaFontAwesome } from 'react-icons/fa'

enum PlayerStates {
    PLAYING = 'PLAYING',
    PAUSED = 'PAUSED',
}

export const Room: React.FunctionComponent = () => {

    const { id } = useParams();

    const [player, setPlayer] = useState<any>();

    const [playerState, setPlayerState] = useState<PlayerStates>();

    const onVideoInit = (e: {target: any}) => {
        setPlayer(e.target);
    }

    const toggleVideo = () => {
        console.log(player)
        if (playerState !== PlayerStates.PLAYING) {
            player?.playVideo();
            setPlayerState(PlayerStates.PLAYING);
        } else if(playerState === PlayerStates.PLAYING) {
            player?.pauseVideo();
            setPlayerState(PlayerStates.PAUSED);
        }
    }

    const skipBack = () => {

    }

    const skipForward = () => {

    }

    const stopVideo = () => {

    }

    return (
        <div className="Room">
            <div className="room-header">
                <RoomHeader/>
            </div>

            <div className="room-body">
                <div className="video-wrapper">
                    <YouTube videoId={id} containerClassName="video" onReady={onVideoInit}/>

                    <div className="video-controls">
                        <Button text='-10s' type='button' clickHandler={skipBack} />
                        <Button text={playerState !== PlayerStates.PLAYING ?'Play' : 'Pause' } type='button' clickHandler={toggleVideo}/>
                        <Button text='Stop' type='button' clickHandler={stopVideo} />
                        <Button text='+10s' type='button' clickHandler={skipForward} />
                    </div>
                </div>

                <div className="chat-wrapper">
                    <UserContext.Consumer>
                        {(value) => (
                            <Chat user={value?.user}/>
                        )}
                    </UserContext.Consumer>
                </div>
            </div>
        </div>
    );
}
